import { ChatContext } from '../context/ChatContext';
import { useContext } from 'react';
import Message from './Message';
const Messages = () => {
  const { messages, id } = useContext(ChatContext);
  return (
    <div className="messages flex col">
      {messages.map((message, i) => {
        if (message.id === id) {
          return (
            <span key={i}>
              <p className="sender user">{message.name}</p>
              <Message message={message.body} author={'user'} />
            </span>
          );
        } else {
          return (
            <span key={i}>
              <p className="sender server">{message.name}</p>
              <Message message={message.body} author={'server'} />
            </span>
          );
        }
      })}
    </div>
  );
};

export default Messages;
