import { createContext, useState, useEffect, useRef } from 'react'
import io from 'socket.io-client';
export const ChatProvider = createContext()

export const ChatContext = ({ children }) => {
    const [id, setId] = useState();
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const socketRef = useRef();
    const sendMessage = (e) => {
        e.preventDefault();
        const messageObject = {
            body: message,
            id
        };
        setMessage('');
        socketRef.current.emit('send message', messageObject);
    }

    const handleChange = (e) => {
        setMessage(e.target.value);
    }


    useEffect(() => {
        socketRef.current = io.connect(`http://${window.location.hostname}:3030`);
        socketRef.current.on('set id', (userId) => {
            setId(userId);
        });

        socketRef.current.on('message', (message) => {
            receivedMessage(message);
        });
    }, []);

    const receivedMessage = (message) => {
        setMessages((oldMsgs) => [...oldMsgs, message]);
    }

    return (
        <ChatContext.Provider
            value={{
                receivedMessage, handleChange, sendMessage, messages, message, id
            }}>
            {children}
        </ChatContext.Provider>
    )
}
