import Controller from './Controller';
import { useContext } from 'react';
const MessageInput = () => {
  const { sendMessage, handleChange, message } = useContext(ChatContext);
  return (
    <div>
      <form onSubmit={sendMessage}>
        <input
          value={message}
          type="text"
          onChange={handleChange}
          placeholder="Say something..."
        />
      </form>
      <Controller onSubmitMessage={onSubmitMessage} />
    </div>
  );
};

export default MessageInput;
