import { ChatContext } from '../context/ChatContext';
import { useContext } from 'react';
import Message from './Message';
import ChatEmojis from './ChatEmojis';
const Messages = () => {
  const { messages, id } = useContext(ChatContext);
  console.log(messages);
  const emojiRules = (message, i) => {
    // if (message.id === messages[i+1].id || )
    console.log(i)
    if (messages[i] === messages[messages.length-1] || messages[i - 1]?.id === message.id) return true
    else return false
  }
  return (
    messages && (
      <div className="messages flex col">
        {messages.map((message, i) => {
          if (message.id === id) {
            return (
              <span className="align-end justify-right" key={i}>
                {messages[i - 1]?.id !== message.id && <p>{message.name}</p>}
                <Message message={message.body} author={'user'} />
                {(messages[i] === messages[messages.length-1] || messages[i - 1]?.id !== message.id) && <ChatEmojis boolean={true} /> }
              </span>
            );
          } else {
            return (
              <span className="align-start" key={i}>
                {messages[i - 1]?.id !== message.id && <p>{message.name}</p>}
                <Message message={message.body} author={'server'} />
              </span>
            );
          }
        })}
      </div>
    )
  );
};

export default Messages;
